      name: name,
      display: display,
      pass: pass,
      age: age,
      gender: gender,
      height: height,
      weight: weight,
      activity: activity
  }
  axios.post('http://localhost:5000/update/profile', user)
  .then(result => console.log(result))
  .catch(err => console.log(err))
  }

  return (
  <>
    <Navbar />
    <div class="btn-group" role="group" aria-label="Basic example">
      <button type="button" class="btn btn-primary" onClick={ () => setDisable(false) }>Edit</button>
      <button type="submit" class="btn btn-primary" onClick={ handleOnSubmit }>Save</button>

    </div>
    <fieldset disabled={disable}>

      <div class="form-check">
      

        <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" checked={gender === 'male' ? true : null}
            onClick={ () => setGender('male') } 
        /> 
      <label class="form-check-label" for="flexRadioDefault1">
          Male
      </label>
      </div>
      <div class="form-check">
      <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2" checked={gender === 'female' ? true : null}
          onClick={ () => setGender('female') } 
      />
      <label class="form-check-label" for="flexRadioDefault2">
          Female
      </label>
      </div>

      <div class="mb-3 row">
          <label for="age" class="col-sm-2 col-form-label">Age</label>
          <div class="col-sm-10">
          <input type="text" class="form-control" id="age" defaultValue={age} onChange={e => setAge(e.target.value)}/>
          </div>
      </div>

      <div class="mb-3 row">
          <label for="weight" class="col-sm-2 col-form-label">Weight</label>
          <div class="col-sm-10">
          <input type="text" class="form-control" id="weight" defaultValue={weight} onChange={e => setWeight(e.target.value)}/>
          </div>
      </div>
    </fieldset>
  </>
  );
}

export default Profile;
