import Navbar from './Navbar'
import { useState, useEffect } from 'react'
import axios from 'axios'

function Profile() {

  const [users, setUsers] = useState([])

  const [gender, setGender] = useState(null)
  const [age, setAge] = useState(users?.age)
  const [weight, setWeight] = useState(users?.weight)
  const [disable, setDisable] = useState(true)


  // useEffect(() => {
  //   axios.get('http://localhost:5000/get')
  //   .then(res => setUsers(res.data))
  //   .catch(err => console.log(err))
  // }, [])
  useEffect( async () => {
    const res = await axios.get('http://localhost:5000/get')
    setUsers(res.data)
  }, [])
  var storageUser = JSON.parse(localStorage.getItem("user")).name
  storageUser = JSON.parse(storageUser)
  
  // var users = users.filter((user) => 
  //   user.name === storageUser
  // )
  // users = users[0]
  var temp = users.filter((user) => 
    user?.name === storageUser
  )
  setUsers(temp)
  setUsers(users[0])

  
  const handleOnSubmit = async (e) => {
    setDisable(true)
  }

  return (
  <>
    <Navbar />
    <div class="btn-group" role="group" aria-label="Basic example">
      <button type="button" class="btn btn-primary" onClick={ () => setDisable(false) }>Edit</button>
      <button type="submit" class="btn btn-primary" onClick={ handleOnSubmit }>Save</button>

    </div>
    <fieldset disabled={disable}>

      <div class="form-check">
      

        <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" checked={users?.gender === 'male' ? true : null}
            onClick={ () => setGender('male') } 
        /> 
      <label class="form-check-label" for="flexRadioDefault1">
          Male
      </label>
      </div>
      <div class="form-check">
      <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2" checked={users?.gender === 'female' ? true : null}
          onClick={ () => {setGender('female')} } 
      />
      <label class="form-check-label" for="flexRadioDefault2">
          Female
      </label>
      </div>

      <div class="mb-3 row">
          <label for="age" class="col-sm-2 col-form-label">Age</label>
          <div class="col-sm-10">
          <input type="text" class="form-control" id="age" defaultValue={users?.age} onChange={e => setAge(e.target.value)}/>
          </div>
      </div>

      <div class="mb-3 row">
          <label for="weight" class="col-sm-2 col-form-label">Weight</label>
          <div class="col-sm-10">
          <input type="text" class="form-control" id="weight" defaultValue={users?.weight} onChange={e => setWeight(e.target.value)}/>
          </div>
      </div>
    </fieldset>
  </>
  );
}

export default Profile;
