import Navbar from './Navbar'
import { useState } from 'react'
import axios from 'axios'

function Account() {
  const [name, setName] = useState(null)
  const [display, setDisplay] = useState(null)
  const [disable, setDisable] = useState(null)

  const handleOnSubmit = async (e) => {
    // setDisable('disable')
    // e.preventDefault();
    
    // axios.post(
    //   'http://localhost:5000/update', {name:'hien', sex, weight})
    //   .then(result => console.log(result))
    //   .catch(err => console.log(err))
  }

  return (
  <>
    <Navbar />
    <div class="btn-group" role="group" aria-label="Basic example">
      <button type="button" class="btn btn-primary" onClick={ () => setDisable(null) }>Edit</button>
      <button type="submit" class="btn btn-primary" onClick={ handleOnSubmit }>Save</button>

    </div>
    <fieldset disabled={disable}>

      <div class="row g-3 align-items-center">
        <div class="col-auto">
          <label for="display" class="col-form-label">Display Name</label>
        </div>

        <div class="col-auto">
        <input type="text" class="form-control" id="display" onChange={e => setDisplay(e.target.value)}/>
        </div>

        <div class="col-auto">
        <button type="button" class="btn btn-primary" onClick={() => {}}>Update</button>
        </div>
      </div>

      <div class="row g-3 align-items-center">
        <div class="col-auto">
          <label for="display" class="col-form-label">Password</label>
        </div>

        <div class="col-auto">
        <input type="text" class="form-control" id="display" onChange={e => setDisplay(e.target.value)}/>
        </div>

        <div class="col-auto">
        <button type="button" class="btn btn-primary" onClick={() => {}}>Update</button>
        </div>
      </div>

    </fieldset>
  </>
  );
}

export default Account;
