import Navbar from './Navbar'
import { useState, useEffect } from 'react'
import axios from 'axios'

function Profile() {

  const [users, setUsers] = useState([])


  useEffect(() => {
    axios.get('http://localhost:5000/get')
    .then(res => setUsers(res.data))
    .catch(err => console.log(err))
  }, [])
  const storageUser = JSON.parse(localStorage.getItem("user")).name
  //storageUser = JSON.parse(storageUser)
  var res = users.filter((user) => 
    user.name === storageUser
  )
  res = res[0]

  const [gender, setGender] = useState(res.gender)
  const [age, setAge] = useState(null)
  const [weight, setWeight] = useState(null)
  const [disable, setDisable] = useState(true)

  const handleOnSubmit = async (e) => {
    setDisable(true)
  }
  console.log(res?.gender)

  return (
  <>
    <Navbar />
    <div class="btn-group" role="group" aria-label="Basic example">
      <button type="button" class="btn btn-primary" onClick={ () => setDisable(false) }>Edit</button>
      <button type="submit" class="btn btn-primary" onClick={ handleOnSubmit }>Save</button>

    </div>
    <fieldset disabled={disable}>

      <div class="form-check">
      

        <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" checked={res?.gender === 'male' ? true : null}
            onClick={ () => setGender('male') } 
        /> 
      <label class="form-check-label" for="flexRadioDefault1">
          Male
      </label>
      </div>
      <div class="form-check">
      <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2" checked={res?.gender === 'female' ? true : null}
          onClick={ () => {setGender('female'); console.log(gender)} } 
      />
      <label class="form-check-label" for="flexRadioDefault2">
          Female
      </label>
      </div>

      <div class="mb-3 row">
          <label for="age" class="col-sm-2 col-form-label">Age</label>
          <div class="col-sm-10">
          <input type="text" class="form-control" id="age" defaultValue={res?.age} onChange={e => setAge(e.target.value)}/>
          </div>
      </div>

      <div class="mb-3 row">
          <label for="weight" class="col-sm-2 col-form-label">Weight</label>
          <div class="col-sm-10">
          <input type="text" class="form-control" id="weight" defaultValue={res?.weight} onChange={e => setWeight(e.target.value)}/>
          </div>
      </div>
    </fieldset>
  </>
  );
}

export default Profile;
