import Navbar from './Navbar'
import { useState } from 'react'
import axios from 'axios'

function Home() {
  const [name, setName] = useState(null)
  const [display, setDisplay] = useState(null)
  const [oldpass, setOldpass] = useState(null)
  const [newpass, setNewpass] = useState(null)
  const [sex, setSex] = useState('male')
  const [age, setAge] = useState(null)
  const [weight, setWeight] = useState(null)
  const [disable, setDisable] = useState(null)

  const handleOnSubmit = async (e) => {
    setDisable('disable')
    e.preventDefault();

    axios.post(
      'http://localhost:5000/update', {name:'hien', sex, weight})
      .then(result => console.log(result))
      .catch(err => console.log(err))
  }

  return (
  <>
    <Navbar />
    <div class="btn-group" role="group" aria-label="Basic example">
      <button type="button" class="btn btn-primary" onClick={ () => setDisable(null) }>Edit</button>
      <button type="submit" class="btn btn-primary" onClick={ handleOnSubmit }>Save</button>

    </div>
    <fieldset disabled={disable}>

      <div class="form-check">

      <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" defaultChecked
          onClick={ () => setSex('male') } 
      />
      <label class="form-check-label" for="flexRadioDefault1">
          Male
      </label>
      </div>
      <div class="form-check">
      <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2"
          onClick={ () => setSex('female') } 
      />
      <label class="form-check-label" for="flexRadioDefault2">
          Female
      </label>
      </div>

      <div class="mb-3 row">
          <label for="age" class="col-sm-2 col-form-label">Age</label>
          <div class="col-sm-10">
          <input type="text" class="form-control" id="age" onChange={e => setAge(e.target.value)}/>
          </div>
      </div>

      <div class="mb-3 row">
          <label for="weight" class="col-sm-2 col-form-label">Weight</label>
          <div class="col-sm-10">
          <input type="text" class="form-control" id="weight" onChange={e => setWeight(e.target.value)}/>
          </div>
      </div>
    </fieldset>
  </>
  );
}

export default Home;
